import { useState } from "react";

export default function App() {
  return (
    <div className="App">
      <Step />
      <Counter />
      <Dates />
    </div>
  );
}

function Step() {
  const [counter, setCounter] = useState(1);

  const handlePlus = () => {
    setCounter(counter + 1);
  };
  const handleMinus = () => {
    setCounter(counter - 1);
  };
  return (
    <>
      <div className="step">
        <button onClick={handleMinus}>-</button>
        <p>Step:{counter}</p>
        <button onClick={handlePlus}>+</button>
      </div>
    </>
  );
}
function Counter() {
  const [step, setStep] = useState(1);

  const handlePlus = () => {
    if (step >= 0 && step <= 100) {
      setStep(step + 1);
    }
  };
  const handleMinus = () => {
    if (step <= 100 && step >= 1) {
      setStep(step - 1);
    }
  };
  return (
    <>
      <div className="counter">
        <button>-</button>
        <p>Counter:1</p>
        <button>+</button>
      </div>
    </>
  );
}
function Dates() {
  const currDate = new Date();
  const currDay = currDate.getDay();
  const days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  const dayOfWeek = days[currDay];
  const newDate = new Date().toLocaleDateString();
  return (
    <>
      <div className="date">
        <p>
          1 days from today is {dayOfWeek} {newDate}
        </p>
      </div>
    </>
  );
}
