import { useState } from "react";

export default function App() {
  return (
    <div className="App">
      <Step />
    </div>
  );
}
function Step() {
  const [step, setStep] = useState(1);

  const handlePlus = () => {
    if (step >= 0 && step <= 100) {
      setStep(step + 1);
    }
  };
  const handleMinus = () => {
    if (step <= 100 && step >= 1) {
      setStep(step - 1);
    }
  };
  return (
    <>
      <div className="step">
        <input
          type="range"
          min={0}
          max={10}
          value={step}
          onChange={(e) => setStep(Number(e.target.value))}
        />
        <p>{step}</p>
      </div>
      <Counter step={step} />
    </>
  );
}
// Logic
function Counter({ step }) {
  const [counter, setCounter] = useState(0);

  const handlePlus = () => {
    setCounter(counter + step);
  };
  const handleMinus = () => {
    setCounter(counter - step);
  };
  return (
    <>
      <div className="counter">
        <button onClick={handleMinus}>-</button>
        <input
          type="text"
          value={counter}
          onChange={(e) => setCounter(Number(e.target.value))}
        />
        <button onClick={handlePlus}>+</button>
      </div>
      <Dates counter={counter} />
    </>
  );
}

function Dates(props) {
  const date = new Date();
  date.setDate(date.getDate() + props.counter);

  const handleRest = () => {
    props.setCounter(0);
    props.step.setStep(1);
  };
  return (
    <>
      <div className="date">
        <p>
          <span>
            {props.counter === 0
              ? "today is"
              : props.counter > 0
              ? `${props.counter} days from today is`
              : `${props.counter} days ago was`}
          </span>{" "}
          <span>{date.toDateString()}</span>{" "}
        </p>
      </div>
      <button className="rest-btn" onChange={() => handleRest()}>
        Rest
      </button>
    </>
  );
}
